
public class ModulusExample {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float firstNumber = 17;
		float secondNumber = 4;
		
		System.out.println(firstNumber + " divided by " + secondNumber);
		System.out.println(firstNumber / secondNumber);
		System.out.println("divides in " + (int)(firstNumber / secondNumber) + " times");
		System.out.println("remainder = " + firstNumber % secondNumber);
		
	}

}
